SELECT artist, COUNT(*) AS entry_count
            FROM {{TABLE_NAME}}
            GROUP BY artist
            ORDER BY entry_count DESC

            LIMIT 10;

SELECT artist, entry_count
FROM (
	SELECT artist, entry_count
	FROM (
		SELECT artist, COUNT(*) AS entry_count
		FROM {{TABLE_NAME}}
		GROUP BY artist
	) AS random_artists
	ORDER BY Random()
	LIMIT 10
)
ORDER BY entry_count DESC;

SELECT *
FROM (
	SELECT *
	FROM (
		SELECT artist || ' - ' || song_name AS combined, COUNT(*) AS entry_count
		FROM {{TABLE_NAME}}
		GROUP BY combined
	) AS random_songs
	ORDER BY Random()
	LIMIT 10
)
ORDER BY entry_count DESC;

SELECT artist || ' - ' || song_name AS combined, COUNT(*) AS entry_count
FROM {{TABLE_NAME}}
GROUP BY combined
ORDER BY entry_count DESC
LIMIT 10;
